    session_start();
    
    if(!isset($_SESSION['role']) || ($_SESSION['role'] != "buyer" && $_SESSION['role'] != "admin")){
        header('Location: .');
        exit;
    }
    
    $conn = new mysqli(getenv('DATABASE_HOST'), getenv('DATABASE_USER'), getenv('DATABASE_PASS'), getenv('DATABASE_NAME'));
    $pPP = 20;
    $currentPage = isset($_GET['page']) ? (int)$_GET['page'] : 1;
    $offset = ($currentPage - 1) * $pPP;
    
    if(isset($_GET['itemid'])){
        
    }
    
    if(isset($_GET['query'])){
        $beforeTerm = htmlspecialchars(urldecode($_GET['query']));
        $searchTerm = "%{$beforeTerm}%";
    
        $stmt = $conn->prepare("SELECT * FROM products WHERE name LIKE ? OR upc LIKE ? LIMIT $pPP OFFSET $offset");
        $stmt->bind_param('ss', $searchTerm, $searchTerm);
        $stmt->execute();
        $result = $stmt->get_result();
    
        $totalStmt = $conn->prepare("SELECT COUNT(*) AS total FROM products WHERE name LIKE ? OR upc LIKE ?");
        $totalStmt->bind_param('ss', $searchTerm, $searchTerm);
        $totalStmt->execute();
        $totalProductsResult = $totalStmt->get_result();
        $totalProducts = $totalProductsResult->fetch_assoc()['total'];
    } else {
        $result = $conn->query("SELECT * FROM products LIMIT $pPP OFFSET $offset");
    
        $totalProductsResult = $conn->query("SELECT COUNT(*) AS total FROM products");
        $totalProducts = $totalProductsResult->fetch_assoc()['total'];
    }
    
    $totalPages = ceil($totalProducts / $pPP);
