        <script>
            document.addEventListener('DOMContentLoaded', function(){
                var currentUrl = new URL(window.location.href);
                var brands = currentUrl.searchParams.get('filterbrands');
                if(brands){
                    brands.split("+").forEach(function(brand){ document.querySelector("a[onclick=\"filterbrand('"+brand+"')\"]").classList = 'active' });
                }
                        
                controller = document.querySelector('#controlorder')
                document.querySelector('#cosubmitorder').addEventListener('click', function(){
                    var xhr = new XMLHttpRequest();
                    xhr.open('POST', '../main/cosubmitorder.php', true);

                    xhr.onreadystatechange = function() {
                        if (xhr.readyState == 4 && xhr.status == 200 && xhr.responseText == "submit") window.location.href = "orders.php";
                    };

                    var data = new FormData(controller);
                    data.append('upc', document.querySelector('#coitemupc').innerText);
                    xhr.send(data);
                })
            });
            document.getElementById('searchForm').addEventListener('submit', function(event) {
                event.preventDefault();
                var searchText = document.getElementById('text-srh').value;
                var query = encodeURIComponent(searchText);
                var currentUrl = new URL(window.location.href);
                currentUrl.searchParams.set('page', 1);
                if(searchText == "") currentUrl.searchParams.delete('query');
                else currentUrl.searchParams.set('query', query);
                window.location.href = currentUrl.href;
            });
            document.getElementById("pageInput").addEventListener("change", function(){
                if (this.value >= 1 && this.value <= <?php echo $totalPages;?>){
                    var currentUrl = new URL(window.location.href);
                    currentUrl.searchParams.set('page', this.value);
                    window.location.href = currentUrl.href;
                }
            });
            function page(a){
                if (a >= 1 && a <= <?php echo $totalPages;?>){
                    var currentUrl = new URL(window.location.href);
                    currentUrl.searchParams.set('page', a);
                    window.location.href = currentUrl.href;
                }
            }
            function filterbrand(a){
                var currentUrl = new URL(window.location.href);
                var brands = currentUrl.searchParams.get('filterbrands');
                if(brands){
                    brands = brands.split("+");
                    if(brands.indexOf(a) != "-1"){
                        if(brands.length == "1"){
                            currentUrl.searchParams.delete('filterbrands');
                        }else{
                            brands.splice(brands.indexOf(a), 1);
                            brands = brands.join("+");
                            currentUrl.searchParams.set('filterbrands', brands);
                        }
                    }else{
                        brands.push(a);
                        brands = brands.join("+");
                        currentUrl.searchParams.set('filterbrands', brands);
                    }
                }else{
                    currentUrl.searchParams.set('filterbrands', a);
                }
                window.location.href = currentUrl.href;
            }
            function openorderpage(a){
                var product = a.getAttribute('item-id');
                
                $.post("catalog", {productid: product})
                .done(function (data){
                    if(data.substring(0, data.indexOf("kill"))){
                        console.og('here');
                    }
                });
            }
        </script>
